         supervisory_error_event.n = "supervisory_error_codes";
         supervisory_error_event.vd = '' + supervisory_error_codes; // TODO make different events for this
         event_array.push(supervisory_error_event);

         var supervisory_sensor_state = bytes[3];
         var supervisory_sensor_state_event = {};
         supervisory_sensor_state_event.n = "supervisory_sensor_state";
         supervisory_sensor_state_event.v = supervisory_sensor_state; // TODO make different events for this
         event_array.push(supervisory_sensor_state_event);

         var battery_event = {};
         var battery_voltage_integer = bytes[4] >>> 4; // integer is highest 4 bits
         var battery_voltage_decimal = (bytes[4] & 0x0F)/10; // decimal is lowest 4 bits
         battery_event.v = battery_voltage_integer + battery_voltage_decimal;
         battery_event.n = "battery_voltage";
         battery_event.u = "V";
         event_array.push(battery_event);
