           air_temp_event.u = "Fah";
         var sign_mask = 0x80;
         var temperature = bytes[3] & 0x7F; // set the sign bit to be 0 so we can have just the temperature
         var temperature_decimal = bytes[4] >>> 4; // 10ths spot is in the upper 4 bits
         air_temp_event.c = temperature + (temperature_decimal/10);// need to take 10ths number and divide by 10 to get it as an actual 10ths place
         air_temp_event.v = (((temperature + (temperature_decimal/10)) *9/5) +32);// need to take 10ths number and divide by 10 to get it as an actual 10ths place
         
         
