// Convert the bytes in payloadBuffer to an array.
var bArray = [];

for ( var i = 0; i < payloadBuffer.length; i++ )
{
    var code = payloadBuffer.charCodeAt(i);

    bArray.push(code & 0xff);
}

node.warn( bArray) ;

// If the payloadBuffer is 11 bytes in length, assume Zurn valve.  

if ( bArray.length >= 11 )
{
    // Compute the sensorId (from the first 4 bytes)
    var sensorId = 0;

    for ( var i = 0; i < 4; i++ )
        sensorId = (sensorId * 0x100) + bArray[i];

    // MessageNum and commandId are just the 5th and
    // 6th byte
    var messageNum = bArray[4];
    var commandId = bArray[5];

    // Copy the fields to an array
    var fields = [];

    for ( var i = 6; i < 10; i++ )
        fields.push(bArray[i]);

    // CRC is the 11th byte
    var crc = bArray[10];

    rmsg.payload.sensorId = sensorId;
    rmsg.payload.messageNum = messageNum;
    rmsg.payload.commandId = commandId;
    rmsg.payload.fields = fields;
    rmsg.payload.crc = crc;

    rmsg.topic = "tgtlora/smartbuildings/watervalve";
}
else if ( bArray.length > 11 ) 
{
    var lux = bArray[5];
}
else
{
    rmsg.payload.payload_raw = payloadBuffer;

    rmsg.topic = "tgt/smartbuildings/json";
}

// Timestamp is part of the original msg, not the payload string
rmsg.payload.timestamp = msg.timestamp;

return rmsg;

