function Decoder(bytes, port) {
var return_object = {};
var event_array = [];
return_object.port = port;
return_object.radio_bridge_protocol_version = bytes[0] >> 4; // first nibble is always 0x1 for Radio Bridge as that is the protocol version
return_object.radio_bridge_packet_count = bytes[0] & 0xF; // second nibble is the packet count (should go up by 1 each time an uplink is sent...even if the uplink is sent 6 times it should increment 6 times)
var message_type = bytes[1]; // Radio Bridge differentiates sensors and other common messages based on this byte
return_object.message_type = message_type;
 switch(message_type) {
    case 0x03: {
 if (bytes.length == 3) {
        var magnet_present_event = {};
 var magnet_sensor_state = Boolean(bytes[2]); // 0x01 means the magnet is NOT present, 0x00 means there IS a magnet
 magnet_present_event.n = "magnet_present";
 magnet_present_event.vb = !magnet_sensor_state;
 event_array.push(magnet_present_event);
 }
 break;
 } // end case 0x03 (door/window sensor)
    case 0x0A: {
      if (bytes.length == 4) {
         var tilt_event = {};
         var tilt_sensor_state = Boolean(bytes[2]); //
      }
      break;
    }
    case 0x00: { // 0x00 is the reset message (5 bytes)
     if (bytes.length == 8) {
     /* Not using right now....should use later
     var sensor_type_codes = {
     0x00: "undefined sensor_type_code",
     0x01: "Door/Window",
     0x02: "Door/Window High Security",
     0x03: "Contact",
     0x04: "Temperature No-Probe",
     0x05: "Temperature External Probe",
     0x06: "Single Push Button",
     0x07: "Dual Push Button",
     0x0E: "Air Temperature and Humidity",
              0x0A: "Tilt Sensor",
     };
     */
     var reset_event = {};
     reset_event.n = "device_reset_event";
     reset_event.vd = '' + bytes;
     /* These are not used right now, but could be used in the future
     var sensor_type_code = bytes[2];
     var hardware_version = bytes[3];
     var firmware_version_major = bytes[4];
     var firmware_version_minor = bytes[5];
     var reset_code_major = bytes[6];
     var reset_code_minor = bytes[7];
     */
     event_array.push(reset_event);
     }
     break;
     } // end case 0x00
     case 0x01: { // 0x01 is the status payload for supervisory messages
     if (bytes.length == 5) {
     var supervisory_error_event = {};
     var supervisory_error_codes = bytes[2];//TODO make enums for this
     supervisory_error_event.n = "supervisory_error_codes";
     supervisory_error_event.vd = '' + supervisory_error_codes; // TODO make different events for this
     event_array.push(supervisory_error_event);

     var supervisory_sensor_state = bytes[3];
     var supervisory_sensor_state_event = {};
     supervisory_sensor_state_event.n = "supervisory_sensor_state";
     supervisory_sensor_state_event.v = supervisory_sensor_state; // TODO make different events for this
     event_array.push(supervisory_sensor_state_event);

     var battery_event = {};
     var battery_voltage_integer = bytes[4] >>> 4; // integer is highest 4 bits
     var battery_voltage_decimal = (bytes[4] & 0x0F)/10; // decimal is lowest 4 bits
     battery_event.v = battery_voltage_integer + battery_voltage_decimal;
     battery_event.n = "battery_voltage";
     battery_event.u = "V";
     event_array.push(battery_event);
     }
     break;
     } // end case 0x01
     case 0x02: { // 0x02 is the status payload for tamper event messages
     if (bytes.length == 3) {
     var tamper_switch_event = {};
     var tamper_switch_closed = Boolean(bytes[2]); // closed means the device is in a secure state. Opened means the case was removed or the sensor was removed from the wall
     tamper_switch_event.n = "tamper_switch_closed";
     tamper_switch_event.vb = tamper_switch_closed; // 0x00 = tamper switch released, 0x01 = tamper switch pressed
     event_array.push(tamper_switch_event);
     }
     break;
     } // end case 0x02
     case 0xfd: { // 0xfd is the test message
     var magnet_event = {}; 
     magnet_event.n = "magnet_device_test";
     magnet_event.v = bytes[2]; // some sensors have more than one byte for status
     event_array.push(magnet_event);
     break;
     } // end case 0xfd
     default: {
     break;
     } // end default case
     } // end switch(message_type)
     if (event_array.length === 0) {
     var unknown_event = {};
     unknown_event.n = "unknown_event";
     unknown_event.vd = '' + bytes;
     event_array.push(unknown_event);
     }
     var raw_data_event = {};
     raw_data_event.n = "raw_data";
     raw_data_event.vd = '' + bytes;
     event_array.push(raw_data_event);
     return_object.events = event_array;
     return return_object;
};

