function Decoder(bytes, port) {
if (bytes.length == 14){  
  var event_array = [];
  var evb_sensors = {};
  var EVB_TYPE = {
    none: 0,
    led_1: 1,
    led_2: 2,
    lux_max: 3,
    lux_min: 4,
    lux: 5,
    barometer_max: 6,
    barometer_min: 7,
    barometer: 8,
    temperature_max: 9,
    temperature__min: 10,
    temperature: 11,
    accelerometer_max: 12,
    accelerometer_min: 13,
    accelerometer: 14,
    tx_interval: 15,
    amps_max: 16,
    amps_min: 17,
    amps: 18,
    m2x_device: 19,
    m2x_key: 20,
  };

/*
 * Process the EVB LoRa payload.
 *
 * EVB payload contains one or more TLV fields.
 *
 * [<type: accelerometer><length: 6><x-high><x-low><y-high><y-low><z-high><z-low>]
 * [<type: barometer><length: 3><byte2><byte1><byte0>]
 * [<type: temperature><length: 2><byte-high><byte-low>]
 * 
 */
  for (var index = 0; index < bytes.length; ) {
    var type = bytes[index++];
  //   var length = bytes[index++];
    var value;
    console.log("type: " + type + " length: " );

    switch (type) {
    case EVB_TYPE.lux:
      if (typeof(evb_sensors.light) == "undefined") {
        evb_sensors.light = {};
      }
      value = bytes[index++] << 8;
      value |= bytes[index++];
      value = value * 0.24;
      evb_sensors.light.lux = value;
      // web of things 
      var lux_lighting_event = {};
      lux_lighting_event.n = "lux_level";
      lux_lighting_event.u = "lx";
      lux_lighting_event.v = evb_sensors.light.lux;
      event_array.push(lux_lighting_event);
      break;
    case EVB_TYPE.barometer:
      if (typeof(evb_sensors.barometer) == "undefined") {
        evb_sensors.barometer = {};
      }
      value = bytes[index++] << 16;
      value |= bytes[index++] << 8;
      value |= bytes[index++];
      value = value * 0.00025;
      evb_sensors.barometer.pa = value;

      //web of things event array payload
      var relative_humidity_event = {};
      relative_humidity_event.n = "relative_humidity";
      relative_humidity_event.u = "%RH";
      relative_humidity_event.v = evb_sensors.barometer.pa;
      event_array.push(relative_humidity_event);

      break;
    case EVB_TYPE.temperature:
      if (typeof(evb_sensors.temperature) == "undefined") {
      evb_sensors.temperature = {};
      }
      value = (bytes[index++] << 24) >> 16;
      value |= bytes[index++];
      value = value * 0.0625;
      evb_sensors.temperature.c = value;
      evb_sensors.temperature.f =  value * 9 / 5 + 32; 
      //web of things event array payload
      var air_temp_event = {};
      air_temp_event.n = "temperature";
      air_temp_event.u = "Fah";
      air_temp_event.v = evb_sensors.temperature.f;
      event_array.push(air_temp_event);

      break;
    case EVB_TYPE.accelerometer:
      if (typeof(evb_sensors.accelerometer) == "undefined") {
      evb_sensors.accelerometer = {};
      }
      // evb_sensors.accelerometer.x = (bytes[index++] << 24) >> 16;
      var x1 = evb_sensors.accelerometer.x = bytes[index++] ;
      // x1 = ~x1 ; 
      // x1 = ( x1 + 1 ) % 256; 
      evb_sensors.accelerometer.x = x1 * 0.0625;//; / 15;
      // evb_sensors.accelerometer.y = (bytes[index++] << 24) >> 16;
      var y1 = evb_sensors.accelerometer.y = bytes[index++] ;
      // y1 = ~ y1 ; 
      // y1 = ( y1 + 1 ) % 256;

      y1 = evb_sensors.accelerometer.y = y1 * 0.0625 ; // / 15 ;
      // evb_sensors.accelerometer.z = (bytes[index++] << 24) >> 16;
      var z1 = evb_sensors.accelerometer.z = bytes[index++] ;
      // z1 = ~ z1 ; 
      // z1 = ( z1 + 1 ) % 256; 
      // z1 = z1 - 128;
      z1 = evb_sensors.accelerometer.z = z1 * 0.0625; // / 15;
      //web of things event array payload
      var accelerometer_x_event = {};
      accelerometer_x_event.n = "x_axis";
      accelerometer_x_event.u = "axis";
      accelerometer_x_event.v = evb_sensors.accelerometer.x;
      event_array.push(accelerometer_x_event);

      var accelerometer_y_event = {};
      accelerometer_y_event.n = "y_axis";
      accelerometer_y_event.u = "axis";
      accelerometer_y_event.v = evb_sensors.accelerometer.y;
      event_array.push(accelerometer_y_event);

      var accelerometer_z_event = {};
      accelerometer_z_event.n = "z_axis";
      accelerometer_z_event.u = "axis";
      accelerometer_z_event.v = evb_sensors.accelerometer.z;
      event_array.push(accelerometer_z_event);
      break;    
      }
  }
  return {
    //temp_c: evb_sensors.temperature.c,
    measurement: "LoRa_demo",
    temp_f: evb_sensors.temperature.f,
    moisture: evb_sensors.barometer.pa,
    lux: evb_sensors.light.lux,
    x_acc: evb_sensors.accelerometer.x,
    y_acc: evb_sensors.accelerometer.y,
    z_acc: evb_sensors.accelerometer.z,
    events: event_array,
  };
} 

if (bytes.length == 11) {

/*
 * Process the EVB LoRa GPS Survey Sweep payload.
 * 
 * Byte 0 is 0
 * Byte 1 is temperature in Celsius
 * Byte 2 is 0
 * Byte 3-6 is GPS Latitude
 * Byte 7-10 is GPS Longitude
 * Bytes 10+ is padding with 0
 * 
*/  
  var gps_event_array = [];

  var temp_c = bytes[1];
  var temp_f = 1.8 * temp_c + 32;
  //web of things event array payload
  var gps_air_temp_event = {};
  gps_air_temp_event.n = "temperature";
  gps_air_temp_event.u = "Fah";
  gps_air_temp_event.v = temp_f;
  gps_event_array.push(gps_air_temp_event);

  var latitude = ((bytes[3] << 24) | (bytes[4] << 16) | (bytes[5] << 8 ) | bytes[6])  / 2147483648*90; // 2^31 * 90
  //web of things event array payload
  var gps_latitude_event = {};
  gps_latitude_event.n = "latitude";
  gps_latitude_event.u = "deg";
  gps_latitude_event.v = latitude;
  gps_event_array.push(gps_latitude_event);

  var longitude = ((bytes[7] << 24) | (bytes[8] << 16) | (bytes[9] << 8 ) | bytes[10]) / 2147483648*180; // 2^31 * 180
  //web of things event array payload
  var gps_longitude_event = {};
  gps_longitude_event.n = "longitude";
  gps_longitude_event.u = "deg";
  gps_longitude_event.v = longitude;
  gps_event_array.push(gps_longitude_event);  

 // if (port === 1) decoded.led = bytes[0];
  return {
    measurement: "GPS_survey",
    events: gps_event_array,
    temp_f: temp_f,
    latitude: latitude,
    longitude: longitude,
  };
} else { return { payload: "unexpected byte length"};  
}
}

